package io.ituknown.crypto;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

import java.security.InvalidKeyException;
import java.security.NoSuchAlgorithmException;
import java.security.SignatureException;
import java.security.spec.InvalidKeySpecException;

public class HashWithRsaTest {
    static final String PLAINTEXT = "hello,world";

    static final String BASE64_PRI_KEY = "";
    static final String BASE64_PUB_KEY = "";

    static final String BASE64_SIGNATURE = "";

    @Test
    public void testSignature() throws Exception {
        String signature = HashWithRsa.SHA256withRSA.signature(BASE64_PRI_KEY, PLAINTEXT, Base64::toString);
        System.out.println(signature);
        Assertions.assertNotNull(signature);
    }

    @Test
    public void testVerify() throws NoSuchAlgorithmException, InvalidKeySpecException, SignatureException, InvalidKeyException {
        boolean verify = HashWithRsa.SHA256withRSA.verify(BASE64_PUB_KEY, PLAINTEXT, Base64.toByte(BASE64_SIGNATURE));
        Assertions.assertTrue(verify);
    }
}